<script src="js/jquery.masonry.min.js"></script><style type="text/css">	#container {		margin: 0 auto;		width: 1100px;		border: 1px solid #333;		-moz-border-radius: 10px;		-webkit-border-radius: 10px;		font-family: Verdana, Geneva, sans-serif;		text-align: center;	}	hovergallery img {		-webkit-transform: scale(0.8); /*Webkit: Scale down image to 0.8x original size*/		-moz-transform: scale(0.8); /*Mozilla scale version*/		-webkit-transition-duration: 0.5s; /*Webkit: Animation duration*/		-moz-transition-duration: 0.5s; /*Mozilla duration version*/		-o-transition-duration: 0.5s; /*Opera duration version*/		opacity: 0.7; /*initial opacity of images*/		margin: 0 10px 20px 0; /*margin between images*/		margin: 8px;		padding: 5px;		border: 5px solid #444;		-webkit-border-radius: 10px;	}	.hovergallery img:hover {		-webkit-transform: scale(1.1); /*Webkit: Scale up image to 1.2x original size*/		-moz-transform: scale(1.1); /*Mozilla scale version*/		-o-transform: scale(1.1); /*Opera scale version*/		box-shadow: 0px 0px 30px gray; /*CSS3 shadow: 30px blurred shadow all around image*/		-webkit-box-shadow: 0px 0px 30px gray; /*Safari shadow version*/		-moz-box-shadow: 0px 0px 30px gray; /*Mozilla shadow version*/		opacity: 1;		border-color: #555;		-moz-box-shadow: 0px 0px 10px #111;		-webkit-box-shadow: 0px 0px 10px #111;	}</style><?php echo !empty($flashmessage) ? '<p class="alert alert-success">' . $flashmessage . '</p>' : ''; ?><ul class="hovergallery">	<div id="container">		<?php		foreach ($images as $row) {			$image = base_url() . "images/galeri/thumbs/" . $row -> thumbnail;			$link = site_url('gallery/detail_foto') . '/' . $row -> id_images;			echo "<a href='$link'><img src='$image'alt='description' /></a>";		}		?></ul></div>